		valueText = valueText.replace(/[\t\n]/g, ' ');
	}
	valueElement.setAttribute('crop', 'end');
	valueElement.setAttribute('value',valueText);
}
else {
	// Wrap to multiple lines
	valueElement.appendChild(document.createTextNode(valueText));
