											multilingualRow,
											lastCreator,
											notYetSaved,
											hasMulti);
					}

					// Move to next or previous field if (shift-)tab was pressed
					if (this._lastTabIndex && this._tabDirection) {
						this._focusNextField('info', this._dynamicFields, this._lastTabIndex, this._tabDirection == -1);
					}
