	this.jurisdictionMenu.appendItem("", "");
}
var data = Zotero.Jurisdiction.getListData();

var pos = 0;
for (var i=0; i<data.length; i++) {
	var value = data[i][0];
	var label = data[i][1];
	var nickname = data[i][2];
	if (nickname) {
		subunits = Zotero.Jurisdiction.getListData(value);
		for (var j=0,jlen=subunits.length;j<jlen;j+=1) {
			this.jurisdictionMenu.appendItem(nickname+": "+subunits[j][1],subunits[j][0])
		}
	} else {
		this.jurisdictionMenu.appendItem(label, value);
	}
